// Admin.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "../styles/admin.css";  // Create and import the CSS file

const Admin = () => {
  const [tours, setTours] = useState([]);
  const [title, setTitle] = useState('');
  const [city, setCity] = useState('');
  const [address, setAddress] = useState('');
  const [distance, setDistance] = useState('');
  const [photo, setPhoto] = useState('');
  const [desc, setDesc] = useState('');
  const [price, setPrice] = useState('');
  const [maxGroupSize, setMaxGroupSize] = useState('');
  const [featured, setFeatured] = useState(false);
  const [page, setPage] = useState(1);

  useEffect(() => {
    fetchTours();
  }, [page]);

  const fetchTours = async () => {
    try {
      const response = await axios.get(`/api/tours?page=${page}`);
      setTours(response.data);
    } catch (error) {
      console.error('Error fetching tours:', error);
    }
  };

  const createTour = async (e) => {
    e.preventDefault();
    try {
      const newTour = {
        title,
        city,
        address,
        distance,
        photo,
        desc,
        price,
        maxGroupSize,
        featured
      };
      await axios.post('/api/tours', newTour);
      fetchTours();
    } catch (error) {
      console.error('Error creating tour:', error);
    }
  };

  return (
    <div className="admin-container">
      <h1>Create Tour</h1>
      <form onSubmit={createTour} className="admin-form">
        <input type="text" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} required />
        <input type="text" placeholder="City" value={city} onChange={(e) => setCity(e.target.value)} required />
        <input type="text" placeholder="Address" value={address} onChange={(e) => setAddress(e.target.value)} required />
        <input type="number" placeholder="Distance" value={distance} onChange={(e) => setDistance(e.target.value)} required />
        <input type="text" placeholder="Photo URL" value={photo} onChange={(e) => setPhoto(e.target.value)} required />
        <textarea placeholder="Description" value={desc} onChange={(e) => setDesc(e.target.value)} required></textarea>
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} required />
        <input type="number" placeholder="Max Group Size" value={maxGroupSize} onChange={(e) => setMaxGroupSize(e.target.value)} required />
        <label>
          <input type="checkbox" checked={featured} onChange={(e) => setFeatured(e.target.checked)} />
          Featured
        </label>
        <button type="submit">Create Tour</button>
      </form>

      <h2>Tours</h2>
      <div className="tours-grid">
        {tours.map((tour) => (
          <div className="tour-card" key={tour.id}>
            <img src={tour.photo} alt={tour.title} />
            <div className="tour-info">
              <h3>{tour.title}</h3>
              <p>{tour.city}</p>
              <p>{tour.price} /per person</p>
            </div>
          </div>
        ))}
      </div>
      <div className="pagination">
        <button onClick={() => setPage(page - 1)} disabled={page === 1}>Previous</button>
        <span>{page}</span>
        <button onClick={() => setPage(page + 1)}>Next</button>
      </div>
    </div>
  );
};

export default Admin;
