import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import "../styles/admin.css";

const AddTour = () => {
  const [tours, setTours] = useState([]);
  const [photo, setPhoto] = useState('');
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [location, setLocation] = useState('');
  const [scheduleId, setScheduleId] = useState('');
  const [description, setDescription] = useState('');
  const [quantity, setQuantity] = useState('');
  const [coachId, setCoachId] = useState('');
  const [numberOfPeopleOnTheTour, setNumberOfPeopleOnTheTour] = useState('');
  const [cateId, setCateId] = useState('');
  const [acId, setAcId] = useState('');

  const [categories, setCategories] = useState([]);
  const [schedules, setSchedules] = useState([]);
  const [coaches, setCoaches] = useState([]);
  const [activities, setActivities] = useState([]);

  const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);
  const [showScheduleDropdown, setShowScheduleDropdown] = useState(false);
  const [showCoachDropdown, setShowCoachDropdown] = useState(false);
  const [showActivityDropdown, setShowActivityDropdown] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const response = await axios.get('http://localhost:3000/tours');
        setTours(response.data.tours);
      } catch (error) {
        console.error('Error fetching tours:', error);
      }
    };

    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:3000/categories');
        setCategories(response.data.categories);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    const fetchSchedules = async () => {
      try {
        const response = await axios.get('http://localhost:3000/schedules');
        setSchedules(response.data.schedules);
      } catch (error) {
        console.error('Error fetching schedules:', error);
      }
    };

    const fetchCoaches = async () => {
      try {
        const response = await axios.get('http://localhost:3000/coaches');
        setCoaches(response.data.coaches);
      } catch (error) {
        console.error('Error fetching coaches:', error);
      }
    };

    const fetchActivities = async () => {
      try {
        const response = await axios.get('http://localhost:3000/activities');
        setActivities(response.data.activities);
      } catch (error) {
        console.error('Error fetching activities:', error);
      }
    };

    fetchTours();
    fetchCategories();
    fetchSchedules();
    fetchCoaches();
    fetchActivities();
  }, []);

  const createTour = async (e) => {
    e.preventDefault();
    try {
      const newTour = {
        photo,
        name,
        price: Math.max(0, price),
        location,
        schedule_id: scheduleId.split(' - ')[0], // Ensure only the ID is sent
        description,
        quantity: Math.max(0, quantity),
        coach_id: coachId.split(' - ')[0], // Ensure only the ID is sent
        number_of_people_on_the_tour: Math.max(0, numberOfPeopleOnTheTour),
        cate_id: cateId.split(' - ')[0], // Ensure only the ID is sent
        ac_id: acId.split(' - ')[0] // Ensure only the ID is sent
      };
      await axios.post('http://localhost:3000/tours', newTour);
      const response = await axios.get('http://localhost:3000/tours');
      setTours(response.data.tours);
      alert('Tour created successfully');
    } catch (error) {
      console.error('Error creating tour:', error);
    }
  };

  const handleCategorySelect = (category) => {
    const formattedCateId = `${category.Cate_id} - ${category.Name}`;
    setCateId(formattedCateId);
    setShowCategoryDropdown(false);
  };

  const handleScheduleSelect = (schedule) => {
    const formattedScheduleId = `${schedule.SC_id} - ${schedule.Location}`;
    setScheduleId(formattedScheduleId);
    setShowScheduleDropdown(false);
  };

  const handleCoachSelect = (coach) => {
    const formattedCoachId = `${coach.Coach_id} - ${coach.Name}`;
    setCoachId(formattedCoachId);
    setShowCoachDropdown(false);
  };

  const handleActivitySelect = (activity) => {
    const formattedActivityId = `${activity.AC_id} - ${activity.Description}`;
    setAcId(formattedActivityId);
    setShowActivityDropdown(false);
  };

  return (
    <div className="admin-container">
      <h1>Create Tour</h1>
      <form onSubmit={createTour} className="admin-form">
        <input type="text" placeholder="Photo URL" value={photo} onChange={(e) => setPhoto(e.target.value)} required />
        <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(Math.max(0, e.target.value))} required />
        <input type="text" placeholder="Location" value={location} onChange={(e) => setLocation(e.target.value)} required />

        <div className="input-with-button">
          <div className="input-with-dropdown">
            <input
              type="text"
              placeholder="Schedule ID"
              value={scheduleId}
              onFocus={() => setShowScheduleDropdown(true)}
              onBlur={() => setShowScheduleDropdown(false)}
              onChange={(e) => setScheduleId(e.target.value)}
              required
            />
            {showScheduleDropdown && (
              <div className="dropdown" onMouseDown={e => e.preventDefault()}>
                {schedules.map(schedule => (
                  <div
                    key={schedule.SC_id}
                    className="dropdown-item"
                    onClick={() => handleScheduleSelect(schedule)}
                  >
                    {schedule.SC_id} - {schedule.Location}
                  </div>
                ))}
              </div>
            )}
          </div>
          <button type="button" onClick={() => navigate('/add-schedule')}>Add Schedule</button>
        </div>

        <textarea placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
        <input type="number" placeholder="Quantity" value={quantity} onChange={(e) => setQuantity(Math.max(0, e.target.value))} required />

        <div className="input-with-button">
          <div className="input-with-dropdown">
            <input
              type="text"
              placeholder="Coach ID"
              value={coachId}
              onFocus={() => setShowCoachDropdown(true)}
              onBlur={() => setShowCoachDropdown(false)}
              onChange={(e) => setCoachId(e.target.value)}
              required
            />
            {showCoachDropdown && (
              <div className="dropdown" onMouseDown={e => e.preventDefault()}>
                {coaches.map(coach => (
                  <div
                    key={coach.Coach_id}
                    className="dropdown-item"
                    onClick={() => handleCoachSelect(coach)}
                  >
                    {coach.Coach_id} - {coach.Name}
                  </div>
                ))}
              </div>
            )}
          </div>
          <button type="button" onClick={() => navigate('/add-coach')}>Add Coach</button>
        </div>

        <input type="number" placeholder="Number Of People On The Tour" value={numberOfPeopleOnTheTour} onChange={(e) => setNumberOfPeopleOnTheTour(Math.max(0, e.target.value))} required />

        <div className="input-with-button">
          <div className="input-with-dropdown">
            <input
              type="text"
              placeholder="Category ID"
              value={cateId}
              onFocus={() => setShowCategoryDropdown(true)}
              onBlur={() => setShowCategoryDropdown(false)}
              onChange={(e) => setCateId(e.target.value)}
              required
            />
            {showCategoryDropdown && (
              <div className="dropdown" onMouseDown={e => e.preventDefault()}>
                {categories.map(category => (
                  <div
                    key={category.Cate_id}
                    className="dropdown-item"
                    onClick={() => handleCategorySelect(category)}
                  >
                    {category.Cate_id} - {category.Name}
                  </div>
                ))}
              </div>
            )}
          </div>
          <button type="button" onClick={() => navigate('/add-category')}>Add Category</button>
        </div>

        <div className="input-with-button">
          <div className="input-with-dropdown">
            <input
              type="text"
              placeholder="Activity ID"
              value={acId}
              onFocus={() => setShowActivityDropdown(true)}
              onBlur={() => setShowActivityDropdown(false)}
              onChange={(e) => setAcId(e.target.value)}
              required
            />
            {showActivityDropdown && (
              <div className="dropdown" onMouseDown={e => e.preventDefault()}>
                {activities.map(activity => (
                  <div
                    key={activity.AC_id}
                    className="dropdown-item"
                    onClick={() => handleActivitySelect(activity)}
                  >
                    {activity.AC_id} - {activity.Description}
                  </div>
                ))}
              </div>
            )}
          </div>
          <button type="button" onClick={() => navigate('/add-activity')}>Add Activity</button>
        </div>

        <button type="submit" className="create-tour-button">Create Tour</button>
      </form>
      <div className="tours-grid">
        {tours.map((tour) => (
          <div className="tour-card" key={tour.Pro_id}>
            <img src={tour.Photo} alt={tour.Name} />
            <div className="tour-info">
              <h3>{tour.Name}</h3>
              <p>{tour.Location}</p>
              <p>${tour.Price} /per person</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AddTour;
