import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import "../styles/admin.css";

const AddActivity = () => {
  const [timeActivity, setTimeActivity] = useState('');
  const [location, setLocation] = useState('');
  const [description, setDescription] = useState('');
  const navigate = useNavigate();

  const createActivity = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/activities', {
        time_activity: timeActivity,
        location,
        description
      });
      alert('Activity created successfully');
      navigate('/admin');
    } catch (error) {
      console.error('Error creating activity:', error);
    }
  };

  return (
    <div className="admin-container">
      <h1>Add Activity</h1>
      <form onSubmit={createActivity} className="admin-form">
        <input type="time" placeholder="Time Activity" value={timeActivity} onChange={(e) => setTimeActivity(e.target.value)} required />
        <input type="text" placeholder="Location" value={location} onChange={(e) => setLocation(e.target.value)} required />
        <input type="text" placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} required />
        <button type="submit">Add Activity</button>
        <button type="button" onClick={() => navigate('/admin')}>Back to Admin Panel</button>
      </form>
    </div>
  );
};

export default AddActivity;
