import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import "../styles/admin.css";

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:3000/categories');
        setCategories(response.data.categories);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };
    fetchCategories();
  }, []);

  const handleEdit = (category) => {
    navigate(`/categories/edit/${category.category_id}`, { state: { category } });
  };

  return (
    <div className="admin-container">
      <h1>Categories</h1>
      <ul className="admin-list">
        {categories.map((category) => (
          <li key={category.category_id} onClick={() => handleEdit(category)}>
            {category.name}
          </li>
        ))}
      </ul>
      <button className="create-tour-button" onClick={() => navigate('/admin')}>Back to Admin Panel</button>
    </div>
  );
};

export default Categories;
