import React, { useRef, useState, useEffect } from "react";
import "../shared/search-bar.css";
import { Col, Form, FormGroup } from "reactstrap";
import { Link } from 'react-router-dom';
import SearchResultList from "../pages/SearchResultList";

const SearchBar = () => {
  const [user, setUser] = useState(null);
  const [results, setResults] = useState([]);
  const locationRef = useRef("");
  const priceRef = useRef(0);
  const maxGroupSizeRef = useRef("");

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUser(storedUser);
    }
  }, []);

  useEffect(() => {
    console.log('User data:', user);
  }, [user]);

  const searchHandler = async () => {
    const location = locationRef.current.value;
    const price = priceRef.current.value;
    const maxGroupSize = maxGroupSizeRef.current.value;

    if (location === "" || price === "" || maxGroupSize === "") {
      return alert("All fields are required");
    }

    try {
      const response = await fetch(`http://localhost:3000/tours?location=${location}&price=${price}&maxGroupSize=${maxGroupSize}`);
      const data = await response.json();

      // Prioritize results based on the number of matches
      const sortedResults = data.tours.sort((a, b) => {
        let aMatches = 0;
        let bMatches = 0;

        if (a.location.includes(location)) aMatches++;
        if (a.price <= price) aMatches++;
        if (a.max_seats >= maxGroupSize) aMatches++;

        if (b.location.includes(location)) bMatches++;
        if (b.price <= price) bMatches++;
        if (b.max_seats >= maxGroupSize) bMatches++;

        return bMatches - aMatches;
      });

      setResults(sortedResults);
    } catch (error) {
      console.error("Error fetching tours:", error);
    }
  };

  return (
    <>
      <Col lg="12">
        <div className="search__bar">
          <Form className="d-flex align-items-center">
            <FormGroup className="d-flex gap-3 form__group form__group-fast">
              <span>
                <i className="ri-map-pin-line"></i>
              </span>
              <div>
                <h6>Location</h6>
                <input
                  type="text"
                  placeholder="Where are you going?"
                  ref={locationRef}
                />
              </div>
            </FormGroup>
            <FormGroup className="d-flex gap-3 form__group form__group-fast">
              <span>
                <i className="ri-money-dollar-circle-line"></i>
              </span>
              <div>
                <h6>Price</h6>
                <input
                  type="number"
                  placeholder="Max Price"
                  ref={priceRef}
                />
              </div>
            </FormGroup>
            <FormGroup className="d-flex gap-3 form__group form__group-last">
              <span>
                <i className="ri-group-line"></i>
              </span>
              <div>
                <h6>Max People</h6>
                <input type="number" placeholder="0" ref={maxGroupSizeRef} />
              </div>
            </FormGroup>
            <span
              className="search__icon"
              type="submit"
              onClick={searchHandler}
            >
              <i className="ri-search-line"></i>
            </span>
          </Form>
        </div>
      </Col>
      <SearchResultList results={results} />
    </>
  );
};

export default SearchBar;
