import React, { useState } from "react";
import { Container, Row, Col, Form, FormGroup, Button } from "reactstrap";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import loginImg from "../assets/images/login.png";
import userIcon from "../assets/images/user.png";
import "../styles/login.css";

const Login = () => {
  const [credentials, setCredentials] = useState({
    email: '',
    password: ''
  });
  const [errorMessages, setErrorMessages] = useState({
    email: '',
    password: ''
  });
  const navigate = useNavigate(); // Get navigate function

  const handleChange = (e) => {
    setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    setErrorMessages({ email: '', password: '' }); // Reset error messages on input change
  };

  const handleClick = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:3002/login', credentials);
      // Redirect to home page
      navigate('/home');
    } catch (err) {
      const errorResponse = err.response.data;
      if (errorResponse.includes('email')|| errorResponse.includes('password')) {
        setErrorMessages((prev) => ({ ...prev, email: 'Incorrect email.' }));
      }

    }
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="8" className="m-auto">
            <div className="login__container d-flex justify-content-between">
              <div className="login__img">
                <img src={loginImg} alt="" />
              </div>
              <div className="login__form">
                <div className="user">
                  <img src={userIcon} alt="" />
                </div>
                <h2>Login</h2>
                <Form onSubmit={handleClick}>
                  <FormGroup>
                    <input
                      type="email"
                      placeholder="Email"
                      required
                      id="email"
                      onChange={handleChange}
                      value={credentials.email}
                    />
                    {errorMessages.email && (
                      <div className="error-message">{errorMessages.email}</div>
                    )}
                  </FormGroup>
                  <FormGroup>
                    <input
                      type="password"
                      placeholder="Password"
                      required
                      id="password"
                      onChange={handleChange}
                      value={credentials.password}
                    />
                    {errorMessages.password && (
                      <div className="error-message">{errorMessages.password}</div>
                    )}
                  </FormGroup>
                  <Button
                    className="btn secondary__btn auth__btn"
                    type="submit"
                  >
                    Login
                  </Button>
                </Form>
                <p>
                  Don't have an account?<Link to="/register">Create</Link>
                </p>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Login;
