import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "../styles/admin.css";

const Admin = () => {
  const [tours, setTours] = useState([]);
  const [Name, setName] = useState('');
  const [Location, setLocation] = useState('');
  const [Pro_Img_id, setProImgId] = useState('');
  const [Description, setDescription] = useState('');
  const [Price, setPrice] = useState('');
  const [Number_Of_People_On_The_Tour, setNumberOfPeopleOnTheTour] = useState('');
  const [featured, setFeatured] = useState(false);

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const response = await axios.get('http://localhost:3000/tours');
        console.log(response.data); // Add this line to log the response
        setTours(response.data.tours);
      } catch (error) {
        console.error('Error fetching tours:', error);
      }
    };

    fetchTours();
  }, []);

  const createTour = async (e) => {
    e.preventDefault();
    try {
      const newTour = {
        Name,
        Location,
        Pro_Img_id,
        Description,
        Price,
        Number_Of_People_On_The_Tour,
        featured
      };
      await axios.post('http://localhost:3000/tours', newTour);
      // Refresh the list of tours
      const response = await axios.get('http://localhost:3000/tours');
      setTours(response.data.tours);
    } catch (error) {
      console.error('Error creating tour:', error);
    }
  };

  return (
    <div className="admin-container">
      <h1>Create Tour</h1>
      <form onSubmit={createTour} className="admin-form">
        <input type="text" placeholder="Title" value={Name} onChange={(e) => setName(e.target.value)} required />
        <input type="text" placeholder="City" value={Location} onChange={(e) => setLocation(e.target.value)} required />
        <input type="text" placeholder="Photo URL" value={Pro_Img_id} onChange={(e) => setProImgId(e.target.value)} required />
        <textarea placeholder="Description" value={Description} onChange={(e) => setDescription(e.target.value)} required></textarea>
        <input type="number" placeholder="Price" value={Price} onChange={(e) => setPrice(e.target.value)} required />
        <input type="number" placeholder="Max Group Size" value={Number_Of_People_On_The_Tour} onChange={(e) => setNumberOfPeopleOnTheTour(e.target.value)} required />
        <label>
          <input type="checkbox" checked={featured} onChange={(e) => setFeatured(e.target.checked)} />
          Featured
        </label>
        <button type="submit">Create Tour</button>
      </form>
      <div className="tours-grid">
        {tours.map((tour) => (
          <div className="tour-card" key={tour.Pro_id}>
            <img src={tour.Pro_Img_id} alt={tour.Name} onError={(e) => { e.target.onerror = null; e.target.src = defaultImage; }} />
            <div className="tour-info">
              <h3>{tour.Name}</h3>
              <p>{tour.Location}</p>
              <p>${tour.Price} /per person</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Admin;
