import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import "../styles/admin.css";

const AddSchedule = () => {
  const [location, setLocation] = useState('');
  const [departureDates, setDepartureDates] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [hotel, setHotel] = useState('');
  const navigate = useNavigate();

  const createSchedule = async (e) => {
    e.preventDefault();
    try {
      const newSchedule = { location, departure_dates: departureDates, return_date: returnDate, hotel };
      await axios.post('http://localhost:3000/schedules', newSchedule);
      alert('Schedule created successfully');
      navigate('/admin');
    } catch (error) {
      console.error('Error creating schedule:', error);
    }
  };

  return (
    <div className="admin-container">
      <h1>Add Schedule</h1>
      <form onSubmit={createSchedule} className="admin-form">
        <input 
          type="text" 
          placeholder="Location" 
          value={location} 
          onChange={(e) => setLocation(e.target.value)} 
          required 
        />
        <input 
          type="datetime-local" 
          placeholder="Departure Dates" 
          value={departureDates} 
          onChange={(e) => setDepartureDates(e.target.value)} 
          required 
        />
        <input 
          type="datetime-local" 
          placeholder="Return Date" 
          value={returnDate} 
          onChange={(e) => setReturnDate(e.target.value)} 
          required 
        />
        <input 
          type="text" 
          placeholder="Hotel" 
          value={hotel} 
          onChange={(e) => setHotel(e.target.value)} 
          required 
        />
        <button type="submit">Add Schedule</button>
        <button type="button" onClick={() => navigate('/admin')}>Back to Add Tour</button>
      </form>
    </div>
  );
};

export default AddSchedule;
