import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import "../styles/admin.css";

const EditTour = () => {
  const { tourId } = useParams();
  const [tour, setTour] = useState({
    name: '',
    description: '',
    startDate: '',
    endDate: '',
    activeDays: '',
    price: '',
    maxSeats: '',
    imageUrl: '',
    status: 'Không hoạt động'
  });
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the tour details
    axios.get(`http://localhost:3000/tours/${tourId}`)
      .then(response => {
        const tourData = response.data;
        setTour({
          name: tourData.name,
          description: tourData.description,
          startDate: tourData.start_date,
          endDate: tourData.end_date,
          activeDays: tourData.active_days,
          price: tourData.price,
          maxSeats: tourData.max_seats,
          imageUrl: tourData.image_url,
          status: tourData.status
        });
      })
      .catch(error => {
        console.error('Error fetching tour details:', error);
      });
  }, [tourId]);

  const updateTour = async (e) => {
    e.preventDefault();
    try {
      const updatedTour = {
        name: tour.name,
        description: tour.description,
        start_date: tour.startDate,
        end_date: tour.endDate,
        active_days: tour.activeDays,
        price: Math.max(0, tour.price),
        max_seats: Math.max(0, tour.maxSeats),
        image_url: tour.imageUrl,
        status: tour.status
      };
      await axios.put(`http://localhost:3000/tours/${tourId}`, updatedTour);
      alert('Tour updated successfully');
      navigate('/admin');
    } catch (error) {
      console.error('Error updating tour:', error);
    }
  };

  const handleChange = (e) => {
    setTour({ ...tour, [e.target.name]: e.target.value });
  };

  return (
    <div className="admin-container">
      <h1>Edit Tour</h1>
      <form onSubmit={updateTour} className="admin-form">
        <input
          type="text"
          placeholder="Name"
          name="name"
          value={tour.name}
          onChange={handleChange}
          required
        />
        <textarea
          placeholder="Description"
          name="description"
          value={tour.description}
          onChange={handleChange}
          required
        ></textarea>
        <input
          type="date"
          placeholder="Start Date"
          name="startDate"
          value={tour.startDate}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          placeholder="End Date"
          name="endDate"
          value={tour.endDate}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          placeholder="Active Days"
          name="activeDays"
          value={tour.activeDays}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          placeholder="Price"
          name="price"
          value={tour.price}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          placeholder="Max Seats"
          name="maxSeats"
          value={tour.maxSeats}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          placeholder="Image URL"
          name="imageUrl"
          value={tour.imageUrl}
          onChange={handleChange}
          required
        />
        <select
          name="status"
          value={tour.status}
          onChange={handleChange}
          required
        >
          <option value="Không hoạt động">Không hoạt động</option>
          <option value="Hoạt động">Hoạt động</option>
          <option value="Đã kết thúc">Đã kết thúc</option>
          <option value="Hết chỗ">Hết chỗ</option>
        </select>
        <button type="submit" className="create-tour-button">Update Tour</button>
      </form>
    </div>
  );
};

export default EditTour;
