import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import "../styles/admin.css";

const AddTour = () => {
  const [tours, setTours] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [activeDays, setActiveDays] = useState('');
  const [price, setPrice] = useState('');
  const [maxSeats, setMaxSeats] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [status, setStatus] = useState('');
  const [scheduleId, setScheduleId] = useState('');

  const [schedules, setSchedules] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchSchedules = async () => {
      try {
        const response = await axios.get('http://localhost:3000/schedules');
        setSchedules(response.data.schedules);
      } catch (error) {
        console.error('Error fetching schedules:', error);
      }
    };

    fetchSchedules();
  }, []);

  const createTour = async (e) => {
    e.preventDefault();
    try {
      const newTour = {
        name,
        description,
        start_date: startDate,
        end_date: endDate,
        active_days: activeDays,
        price: parseFloat(price),
        max_seats: parseInt(maxSeats),
        image_url: imageUrl,
        status
      };
      await axios.post(`http://localhost:3000/schedules/${scheduleId}/tours`, newTour);
      alert('Tour created successfully');
      navigate('/admin');
    } catch (error) {
      console.error('Error creating tour:', error);
    }
  };

  const handleScheduleSelect = (schedule) => {
    setScheduleId(schedule.itinerary_id);
  };

  return (
    <div className="admin-container">
      <h1>Create Tour</h1>
      <form onSubmit={createTour} className="admin-form">
        <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
        <textarea placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
        <input type="date" placeholder="Start Date" value={startDate} onChange={(e) => setStartDate(e.target.value)} required />
        <input type="date" placeholder="End Date" value={endDate} onChange={(e) => setEndDate(e.target.value)} required />
        <input type="number" placeholder="Active Days" value={activeDays} onChange={(e) => setActiveDays(e.target.value)} required />
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} required />
        <input type="number" placeholder="Max Seats" value={maxSeats} onChange={(e) => setMaxSeats(e.target.value)} required />
        <input type="text" placeholder="Image URL" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)} required />
        <input type="text" placeholder="Status" value={status} onChange={(e) => setStatus(e.target.value)} required />

        <div className="input-with-button">
          <div className="input-with-dropdown">
            <input
              type="text"
              placeholder="Schedule ID"
              value={scheduleId}
              onFocus={() => setShowScheduleDropdown(true)}
              onBlur={() => setShowScheduleDropdown(false)}
              onChange={(e) => setScheduleId(e.target.value)}
              required
            />
            {showScheduleDropdown && (
              <div className="dropdown" onMouseDown={e => e.preventDefault()}>
                {schedules.map(schedule => (
                  <div
                    key={schedule.itinerary_id}
                    className="dropdown-item"
                    onClick={() => handleScheduleSelect(schedule)}
                  >
                    {schedule.itinerary_id} - {schedule.location}
                  </div>
                ))}
              </div>
            )}
          </div>
          <button type="button" onClick={() => navigate('/add-schedule')}>Add Schedule</button>
        </div>

        <button type="submit" className="create-tour-button">Create Tour</button>
      </form>
      <div className="tours-grid">
        {tours.map((tour) => (
          <div className="tour-card" key={tour.tour_id}>
            <img src={tour.image_url} alt={tour.name} />
            <div className="tour-info">
              <h3>{tour.name}</h3>
              <p>{tour.location}</p>
              <p>${tour.price} /per person</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AddTour;
