import React, { useContext, useState, useEffect } from 'react';
import AuthContext from '../context/AuthContext';
import "../styles/profile.css";

const Profile = () => {
    const { user: authUser, updateUser } = useContext(AuthContext); // Include updateUser method
    const [user, setUser] = useState(null);
    const [editMode, setEditMode] = useState(false);
    const [profileData, setProfileData] = useState({
        fullname: '',
        gender: '',
        birthday: '',
        username: '',
        photo: ''
    });

    useEffect(() => {
        const fetchUserProfile = async () => {
            const response = await fetch(`/user/profile?phone=${authUser.phone}`);
            const userData = await response.json();
            setUser(userData);
            setProfileData({
                fullname: userData.FullName,
                gender: userData.Gender,
                birthday: userData.Birthday,
                username: userData.Username,
                photo: userData.Photo
            });
        };

        fetchUserProfile();
    }, [authUser.phone]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProfileData({
            ...profileData,
            [name]: value
        });
    };

    const handleSave = async () => {
        const response = await fetch('/user/update', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                phone: authUser.phone,
                fullname: profileData.fullname,
                gender: profileData.gender,
                birthday: profileData.birthday
            })
        });

        if (response.ok) {
            // Update was successful
            alert('Profile updated successfully');
            setEditMode(false);
            const updatedUser = {
                ...user,
                FullName: profileData.fullname,
                Gender: profileData.gender,
                Birthday: profileData.birthday
            };
            setUser(updatedUser);
            updateUser(updatedUser); // Update the context state
        } else {
            // Handle error
            alert('Error updating profile');
        }
    };

    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <div className="profile-container">
            <div className="profile-header">
                <img className="profile-avatar" src={profileData.photo || "/images/ava-1.jpg"} alt="Avatar" />
                <h1 className="profile-username">{profileData.username}</h1>
            </div>
            <div className="profile-details">
                <h2 className="profile-section-title">Thông tin tài khoản</h2>
                <div className="profile-info">
                    <div className="profile-info-item">
                        <span className="profile-info-label">Phone:</span>
                        <span className="profile-info-value">{authUser.phone}</span>
                    </div>
                    <div className="profile-info-item">
                        <span className="profile-info-label">Full Name:</span>
                        {editMode ? (
                            <input
                                type="text"
                                name="fullname"
                                value={profileData.fullname}
                                onChange={handleChange}
                            />
                        ) : (
                            <span className="profile-info-value">{profileData.fullname}</span>
                        )}
                    </div>
                    <div className="profile-info-item">
                        <span className="profile-info-label">Gender:</span>
                        {editMode ? (
                            <input
                                type="text"
                                name="gender"
                                value={profileData.gender}
                                onChange={handleChange}
                            />
                        ) : (
                            <span className="profile-info-value">{profileData.gender}</span>
                        )}
                    </div>
                    <div className="profile-info-item">
                        <span className="profile-info-label">Birthday:</span>
                        {editMode ? (
                            <input
                                type="date"
                                name="birthday"
                                value={profileData.birthday}
                                onChange={handleChange}
                            />
                        ) : (
                            <span className="profile-info-value">{profileData.birthday}</span>
                        )}
                    </div>
                </div>
                {editMode ? (
                    <button className="btn save-btn" onClick={handleSave}>Save</button>
                ) : (
                    <button className="btn edit-btn" onClick={() => setEditMode(true)}>Edit</button>
                )}
            </div>
        </div>
    );
};

export default Profile;
