import React, { useState, useEffect } from "react";
import CommonSection from "../shared/CommonSection";
import tourData from "../assets/data/tours.js";
import Newletters from "../shared/Newletters";
import SearchBar from "../shared/SearchBar";
import { Col, Container, Row, Card, CardBody } from "reactstrap";
import { Link } from "react-router-dom";
import "../tour.css";

const Tour = () => {
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);

  useEffect(() => {
    const pages = Math.ceil(5 / 8); //later we will use backend data count
    setPageCount(pages);
  }, [page]);

  const TourCard = ({ tour }) => {
    const { id, title, city, photo, price, featured, reviews } = tour;
    const avgRating = reviews.length > 0
      ? (reviews.reduce((sum, review) => sum + review.rating, 0) / reviews.length).toFixed(1)
      : 0;

    return (
      <div className="tour__card">
        <Card>
          <div className="tour__img">
            <img src={photo} alt={title} />
            {featured && <span>Featured</span>}
          </div>
          <CardBody>
            <div className="card__top d-flex align-items-center justify-content-between">
              <span className="tour__location d-flex align-items-center gap-1">
                <i className="ri-map-pin-line"></i> {city}
              </span>
              <span className="tour__rating d-flex align-items-center gap-1">
                <i className="ri-star-fill"></i> {avgRating}
                {reviews.length > 0 && <span>({reviews.length})</span>}
              </span>
            </div>
            <h5 className="tour__title">
              <Link to={`/tours/${id}`}>{title}</Link>
            </h5>
            <div className="card__bottom d-flex align-items-center justify-content-between mt-3">
              <h5>
                ${price} <span> /per person</span>
              </h5>
              <button className="btn booking__btn">
                <Link to={`/tours/${id}`}>Book Now</Link>
              </button>
            </div>
          </CardBody>
        </Card>
      </div>
    );
  };

  return (
    <>
      <CommonSection title={"All Tours"} />
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className="pt-0">
        <Container>
          <Row>
            {tourData?.map((tour) => (
              <Col lg="3" className="mb-4" key={tour.id}>
                <TourCard tour={tour} />
              </Col>
            ))}
            <Col lg="12">
              <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                {[...Array(pageCount).keys()].map((number) => (
                  <span
                    key={number}
                    onClick={() => setPage(number)}
                    className={page === number ? "active__page" : ""}
                  >
                    {number + 1}
                  </span>
                ))}
              </div>
            </Col>
          </Row>
        </Container>
      </section>
      <Newletters />
    </>
  );
};

export default Tour;
