import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import "../styles/admin.css";

const AddCoach = () => {
  const [name, setName] = useState('');
  const [sdt, setSdt] = useState('');
  const [gender, setGender] = useState('');
  const navigate = useNavigate();

  const createCoach = async (e) => {
    e.preventDefault();
    try {
      const newCoach = { name, sdt, gender };
      await axios.post('http://localhost:3000/coaches', newCoach);
      alert('Coach created successfully');
      navigate('/admin');
    } catch (error) {
      console.error('Error creating coach:', error);
    }
  };

  return (
    <div className="admin-container">
      <h1>Add Coach</h1>
      <form onSubmit={createCoach} className="admin-form">
        <input 
          type="text" 
          placeholder="Name" 
          value={name} 
          onChange={(e) => setName(e.target.value)} 
          required 
        />
        <input 
          type="text" 
          placeholder="Phone Number" 
          value={sdt} 
          onChange={(e) => setSdt(e.target.value)} 
          required 
        />
        <input 
          type="text" 
          placeholder="Gender" 
          value={gender} 
          onChange={(e) => setGender(e.target.value)} 
          required 
        />
        <button type="submit">Add Coach</button>
        <button type="button" onClick={() => navigate('/admin')}>Back to Add Tour</button>
      </form>
    </div>
  );
};

export default AddCoach;
