import React, { useState, useEffect } from "react";
import CommonSection from "../shared/CommonSection";
import Newletters from "../shared/Newletters";
import SearchBar from "../shared/SearchBar";
import { Col, Container, Row, Card, CardBody } from "reactstrap";
import { Link } from "react-router-dom";
import "../styles/tour.css";

const Tour = () => {
  const [tours, setTours] = useState([]);
  const [userProfile, setUserProfile] = useState(null);
  const requiredToursCount = 8; // Number of tours to display on one page

  useEffect(() => {
    fetch(`http://localhost:3000/tours`)
      .then(response => response.json())
      .then(data => {
        setTours(data.tours);
      });
  }, []);

  useEffect(() => {
    // Fetch user profile
    const token = localStorage.getItem("token"); // Assume the JWT is stored in localStorage
    if (token) {
      fetch(`http://localhost:3000/user/profile`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(response => response.json())
        .then(data => {
          console.log('User profile fetched:', data);
          setUserProfile(data);
        })
        .catch(error => {
          console.error('Error fetching user profile:', error);
        });
    } else {
      console.log('No token found');
    }
  }, []);

  const TourCard = ({ tour }) => {
    const { tour_id, name, image_url, price, max_seats, avg_rating, reviews = [], status } = tour;
    const defaultImage = "https://img.freepik.com/free-photo/painting-mountain-lake-with-mountain-background_188544-9126.jpg";
    const canEdit = status === 'Không hoạt động' || status === 'Đã kết thúc';

    console.log(`Tour ${tour_id} - Status: ${status} - Can edit: ${canEdit}`);

    return (
      <div className="tour__card">
        <Card>
          <div className="tour__img">
            <img 
              src={image_url || defaultImage} 
              alt={name} 
              onError={(e) => { e.target.onerror = null; e.target.src = defaultImage; }} 
            />
            {max_seats > 0 && <span>Featured</span>}
          </div>
          <CardBody>
            <div className="card__top d-flex align-items-center justify-content-between">
              <span className="tour__location d-flex align-items-center gap-1">
                <i className="ri-map-pin-line"></i> {tour.location}
              </span>
              <span className="tour__rating d-flex align-items-center gap-1">
                <i className="ri-star-fill"></i> {avg_rating || 0}
                {reviews.length > 0 && <span>({reviews.length})</span>}
              </span>
            </div>
            <h5 className="tour__title">
              <Link to={`/tours/${tour_id}`}>{name}</Link>
            </h5>
            <div className="card__bottom d-flex align-items-center justify-content-between mt-3">
              <h5>
                $<Link to={`/tours/${tour_id}`}>{Math.floor(price)}</Link> <span> /per person</span>
              </h5>
              <div className="d-flex flex-column align-items-center">
                {userProfile && userProfile.role === "NVQL_CT" && canEdit && (
                  <button className="btn edit__btn mb-2">
                    <Link to={`/tours/edit/${tour_id}`}>Edit</Link>
                  </button>
                )}
                <button className="btn booking__btn">
                  <Link to={`/tours/${tour_id}`}>Book Now</Link>
                </button>
              </div>
            </div>
          </CardBody>
        </Card>
      </div>
    );
  };

  const filterAndFillTours = (tours, userProfile) => {
    let filteredTours = [];
    if (!userProfile || userProfile.role === "Khách") {
      filteredTours = tours.filter(tour => tour.status === 'Hoạt động' || tour.status === 'Hết chỗ');
      if (filteredTours.length < requiredToursCount) {
        const additionalTours = tours.filter(tour => !filteredTours.includes(tour)).slice(0, requiredToursCount - filteredTours.length);
        filteredTours = [...filteredTours, ...additionalTours];
      }
    } else {
      filteredTours = tours;
    }
    return filteredTours;
  };

  const filteredTours = filterAndFillTours(tours, userProfile);

  return (
    <>
      <CommonSection title={"All Tours"} />
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className="pt-0">
        <Container>
          <Row>
            {filteredTours?.map((tour) => (
              <Col lg="3" className="mb-4" key={tour.tour_id}>
                <TourCard tour={tour} />
              </Col>
            ))}
          </Row>
        </Container>
      </section>
      <Newletters />
    </>
  );
};

export default Tour;
