import React, { useState, useEffect } from "react";
import CommonSection from "../shared/CommonSection";
import Newletters from "../shared/Newletters";
import { Col, Container, Row, Card, CardBody } from "reactstrap";
import { Link } from "react-router-dom";
import "../styles/tour.css";

const ActiveToursItinerary = () => {
  const [itineraries, setItineraries] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:3000/tours/itineraries?status=active`)
      .then(response => response.json())
      .then(data => {
        setItineraries(data.itineraries);
      })
      .catch(error => {
        console.error('Error fetching tour itineraries:', error);
      });
  }, []);

  const ItineraryCard = ({ itinerary }) => {
    const { tour_id, day_number, activity_description, start_time, end_time } = itinerary;

    return (
      <Card className="mb-3">
        <CardBody>
          <h5>Tour ID: {tour_id}</h5>
          <p>Day: {day_number}</p>
          <p>Activity: {activity_description}</p>
          <p>Start Time: {start_time}</p>
          <p>End Time: {end_time}</p>
        </CardBody>
      </Card>
    );
  };

  return (
    <>
      <CommonSection title={"Active Tours Itineraries"} />
      <section>
        <Container>
          <Row>
            {itineraries.map((itinerary) => (
              <Col lg="4" key={itinerary.itinerary_id}>
                <ItineraryCard itinerary={itinerary} />
              </Col>
            ))}
          </Row>
        </Container>
      </section>
      <Newletters />
    </>
  );
};

export default ActiveToursItinerary;
