import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import "../styles/admin.css";

const EditTour = () => {
  const { tourId } = useParams();
  const [tour, setTour] = useState({
    name: '',
    description: '',
    startDate: '',
    endDate: '',
    price: '',
    maxSeats: '',
    imageUrl: '',
    status: 'Không hoạt động',
  });
  const [categories, setCategories] = useState([]);
  const [locations, setLocations] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedLocation, setSelectedLocation] = useState('');
  const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);
  const [showLocationDropdown, setShowLocationDropdown] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTourDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/tours/${tourId}`);
        const tourData = response.data;
        setTour({
          name: tourData.name,
          description: tourData.description,
          startDate: tourData.start_date,
          endDate: tourData.end_date,
          price: tourData.price,
          maxSeats: tourData.max_seats,
          imageUrl: tourData.image_url,
          status: tourData.status,
        });
        setSelectedCategory(tourData.category_id); // Assuming your tour data includes category_id
        setSelectedLocation(tourData.location_id); // Assuming your tour data includes location_id
      } catch (error) {
        console.error('Error fetching tour details:', error);
      }
    };

    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:3000/categories');
        setCategories(response.data.categories);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    const fetchLocations = async () => {
      try {
        const response = await axios.get('http://localhost:3000/locations');
        setLocations(response.data.locations);
      } catch (error) {
        console.error('Error fetching locations:', error);
      }
    };

    fetchTourDetails();
    fetchCategories();
    fetchLocations();
  }, [tourId]);

  const updateTour = async (e) => {
    e.preventDefault();
    try {
      const updatedTour = {
        name: tour.name,
        description: tour.description,
        start_date: tour.startDate,
        end_date: tour.endDate,
        price: Math.max(0, tour.price),
        max_seats: Math.max(0, tour.maxSeats),
        image_url: tour.imageUrl,
        status: tour.status,
      };
      await axios.put(`http://localhost:3000/tours/${tourId}`, updatedTour);

      // Update tour-category table
      if (selectedCategory) {
        await axios.post('http://localhost:3000/tour-categories', {
          tour_id: tourId,
          category_id: selectedCategory,
        });
      }

      // Update tour-location table
      if (selectedLocation) {
        await axios.post('http://localhost:3000/tour-locations', {
          tour_id: tourId,
          location_id: selectedLocation,
        });
      }

      alert('Tour updated successfully');
      navigate('/admin');
    } catch (error) {
      console.error('Error updating tour:', error);
    }
  };

  const handleChange = (e) => {
    setTour({ ...tour, [e.target.name]: e.target.value });
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category.category_id);
  };

  const handleLocationSelect = (location) => {
    setSelectedLocation(location.location_id);
  };

  return (
    <div className="admin-container">
      <h1>Edit Tour</h1>
      <form onSubmit={updateTour} className="admin-form">
        <input
          type="text"
          placeholder="Name"
          name="name"
          value={tour.name}
          onChange={handleChange}
          required
        />
        <textarea
          placeholder="Description"
          name="description"
          value={tour.description}
          onChange={handleChange}
          required
        ></textarea>
        <input
          type="date"
          placeholder="Start Date"
          name="startDate"
          value={tour.startDate}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          placeholder="End Date"
          name="endDate"
          value={tour.endDate}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          placeholder="Price"
          name="price"
          value={tour.price}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          placeholder="Max Seats"
          name="maxSeats"
          value={tour.maxSeats}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          placeholder="Image URL"
          name="imageUrl"
          value={tour.imageUrl}
          onChange={handleChange}
          required
        />
        <select
          name="status"
          value={tour.status}
          onChange={handleChange}
          required
        >
          <option value="Không hoạt động">Không hoạt động</option>
          <option value="Hoạt động">Hoạt động</option>
          <option value="Đã kết thúc">Đã kết thúc</option>
          <option value="Hết chỗ">Hết chỗ</option>
        </select>

        <div className="input-with-dropdown">
          <input
            type="text"
            placeholder="Select Category"
            value={selectedCategory}
            onFocus={() => setShowCategoryDropdown(true)}
            onBlur={() => setShowCategoryDropdown(false)}
            onChange={(e) => setSelectedCategory(e.target.value)}
            required
          />
          {showCategoryDropdown && (
            <div className="dropdown" onMouseDown={(e) => e.preventDefault()}>
              {categories.map((category) => (
                <div
                  key={category.category_id}
                  className="dropdown-item"
                  onClick={() => handleCategorySelect(category)}
                >
                  {category.category_id} - {category.name}
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="input-with-dropdown">
          <input
            type="text"
            placeholder="Select Location"
            value={selectedLocation}
            onFocus={() => setShowLocationDropdown(true)}
            onBlur={() => setShowLocationDropdown(false)}
            onChange={(e) => setSelectedLocation(e.target.value)}
            required
          />
          {showLocationDropdown && (
            <div className="dropdown" onMouseDown={(e) => e.preventDefault()}>
              {locations.map((location) => (
                <div
                  key={location.location_id}
                  className="dropdown-item"
                  onClick={() => handleLocationSelect(location)}
                >
                  {location.location_id} - {location.name}
                </div>
              ))}
            </div>
          )}
        </div>

        <button type="submit" className="create-tour-button">Update Tour</button>
      </form>
    </div>
  );
};

export default EditTour;
