import React, { useState, useEffect, useContext } from "react";
import { Container, Row, Col, Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap";
import { useParams, useNavigate } from "react-router-dom";
import avatar from "../assets/images/avatar.jpg";
import Newletters from "./../shared/Newletters";
import AuthContext from "../context/AuthContext";
import "../styles/tour-details.css";

const Booking = ({ tour, avgRating }) => {
  const { price, reviews } = tour;
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  const [credentials, setCredentials] = useState({
    userId: "",
    fullName: "",
    phone: "",
    guestSize: 1,
    bookAt: "",
    passengers: []
  });

  // Update credentials state when user context changes
  useEffect(() => {
    if (user) {
      setCredentials((prev) => ({
        ...prev,
        userId: user.user_id,
        fullName: user.name,
        phone: user.phone_number,
      }));
    }
  }, [user]);

  useEffect(() => {
    console.log("User:", user); // Debugging line to check user context
    console.log("User credentials:", credentials); // Debugging line to check credentials state
  }, [user, credentials]);

  const handleChange = (e) => {
    setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleGuestChange = (e) => {
    const guestSize = parseInt(e.target.value);
    const passengers = [...Array(guestSize)].map((_, index) => ({
      name: "",
      birth_date: "",
      gender: "Nam"
    }));
    setCredentials((prev) => ({ ...prev, guestSize, passengers }));
  };

  const handlePassengerChange = (index, e) => {
    const updatedPassengers = [...credentials.passengers];
    updatedPassengers[index][e.target.id] = e.target.value;
    setCredentials((prev) => ({ ...prev, passengers: updatedPassengers }));
  };

  const serviceFee = 10;
  const totalAmount = Math.floor(Number(price) * Number(credentials.guestSize) + Number(serviceFee));

  const handleClick = (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Token is missing. Please log in again.");
      return;
    }
    fetch('http://localhost:3000/book', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify(credentials)
    })
      .then(response => {
        if (response.ok) {
          navigate("/thank-you");
        } else {
          alert("Booking failed. Please try again.");
        }
      });
  };

  return (
    <div className="booking">
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>
          ${Math.floor(price)} <span>/per person</span>
        </h3>
        <span className="tour__rating d-flex align-items-center gap-1">
          <i className="ri-star-fill"></i>
          {avgRating === 0 ? null : avgRating} ({reviews?.length})
        </span>
      </div>
      <div className="booking__form">
        <h5>Information</h5>
        <Form className="booking__info-form" onSubmit={handleClick}>
          <FormGroup>
            <input type="text" placeholder="Full Name" id="fullName" required value={credentials.fullName} readOnly />
          </FormGroup>
          <FormGroup>
            <input type="text" placeholder="Phone" id="phone" required value={credentials.phone} readOnly />
          </FormGroup>
          <FormGroup className="d-flex align-items-center gap-3">
            <input type="date" id="bookAt" required onChange={handleChange} />
            <input type="number" placeholder="Guest" id="guestSize" required onChange={handleGuestChange} />
          </FormGroup>
          {credentials.passengers.map((passenger, index) => (
            <div key={index}>
              <h6>Passenger {index + 1}</h6>
              <FormGroup>
                <input type="text" placeholder="Name" id="name" required value={passenger.name} onChange={(e) => handlePassengerChange(index, e)} />
              </FormGroup>
              <FormGroup>
                <input type="date" placeholder="Birth Date" id="birth_date" required value={passenger.birth_date} onChange={(e) => handlePassengerChange(index, e)} />
              </FormGroup>
              <FormGroup>
                <select id="gender" required value={passenger.gender} onChange={(e) => handlePassengerChange(index, e)}>
                  <option value="Nam">Nam</option>
                  <option value="Nữ">Nữ</option>
                </select>
              </FormGroup>
            </div>
          ))}
        </Form>
      </div>
      <div className="booking__bottom">
        <ListGroup>
          <ListGroupItem className="border-0 px-0">
            <h5 className="d-flex align-items-center gap-1">
              ${Math.floor(price)}
              <i className="ri-close-line"></i> 1 person
            </h5>
            <span> ${Math.floor(price)}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0">
            <h5>Service charge</h5>
            <span> ${serviceFee}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0 total">
            <h5>Total</h5>
            <span> ${totalAmount}</span>
          </ListGroupItem>
        </ListGroup>
        <Button className="btn primary__btn w-100 mt-4" type="submit" onClick={handleClick}>
          Book Now
        </Button>
      </div>
    </div>
  );
};

const TourDetails = () => {
  const { id } = useParams();
  const [tour, setTour] = useState(null);
  const [tourRating, setTourRating] = useState(null);
  const [buttonText, setButtonText] = useState("Submit");
  const [userId, setUserId] = useState(""); // To hold user ID dynamically
  const [fullname, setFullName] = useState("");
  useEffect(() => {
    const token = localStorage.getItem("token"); // assuming token is stored in localStorage
    fetch(`http://localhost:3000/tours/${id}`)
      .then(response => response.json())
      .then(data => setTour(data));

    // Fetch user profile data
    fetch(`http://localhost:3000/user/profile`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(response => response.json())
      .then(data => {
        console.log("User Profile Data:", data); // Debugging line
        setUserId(data.user_id);
        setFullName(data.fullName);
      });
  }, [id]);

  const submitHandler = async (e) => {
    e.preventDefault();
    const review = {
      user_id: userId,
      tour_id: id,
      rating: tourRating,
      review_date: new Date().toISOString().split('T')[0]
    };

    await fetch(`http://localhost:3000/reviews`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(review)
    });

    // Fetch updated tour data
    const updatedTour = await fetch(`http://localhost:3000/tours/${id}`).then(response => response.json());
    setTour(updatedTour);
    setTourRating(null);
    setButtonText("Submitted");
  };

  const handleRatingClick = (rating) => {
    setTourRating(rating);
    setButtonText("Submit");
  };

  if (!tour) return <div>Loading...</div>;

  const {
    image_url,
    name,
    description,
    price,
    address,
    reviews,
    start_date,
    end_date,
    max_seats,
    seats_remaining,
  } = tour;

  const calculalteAvgRating = (reviews) => {
    const totalRating = reviews.reduce((acc, review) => acc + review.rating, 0);
    const avgRating = totalRating / reviews.length;
    return { totalRating, avgRating };
  };

  const { totalRating, avgRating } = calculalteAvgRating(reviews);

  const options = { day: "numeric", month: "long", year: "numeric" };

  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg="8">
              <div className="tour__content">
                <img src={image_url} alt="" />
                <div className="tour__info">
                  <h2>{name}</h2>
                  <div className="d-flex align-items-center gap-5">
                    <span className="tour__rating d-flex align-items-center gap-1">
                      <i className="ri-star-fill" style={{ color: "var(--secondary-color)" }}></i>
                      {avgRating === 0 ? null : avgRating}
                      {totalRating === 0 ? "Not rated" : <span>({reviews?.length})</span>}
                    </span>
                    <span>
                      <i className="ri-map-pin-user-fill"></i> {address}
                    </span>
                  </div>
                  <div className="tour__extra-details">
                    <span>
                      <i className="ri-calendar-line"></i> Start Date: {new Date(start_date).toLocaleDateString("en-US", options)}
                    </span>
                    <span>
                      <i className="ri-calendar-line"></i> End Date: {new Date(end_date).toLocaleDateString("en-US", options)}
                    </span>
                    <span>
                      <i className="ri-money-dollar-circle-line"></i> {Math.floor(price)} /per person
                    </span>
                    <span>
                      <i className="ri-group-line"></i> Remaining seats: {seats_remaining}
                    </span>
                  </div>
                  <h5>Description</h5>
                  <p>{description}</p>
                </div>
                <div className="tour__reviews mt-4">
                  <h4>Reviews({reviews?.length} reviews)</h4>
                  <Form onSubmit={submitHandler}>
                    <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                      {[...Array(6)].map((_, index) => (
                        <span key={index} onClick={() => handleRatingClick(index)}>
                          {index} <i className="ri-star-fill"></i>
                        </span>
                      ))}
                    </div>
                    <Button type="submit" className="btn primary__btn text-white">{buttonText}</Button>
                  </Form>
                  <ListGroup className="user__reviews">
                    {reviews?.map((review) => (
                      <div className="review__item" key={review.review_id}>
                        <img src={avatar} alt="" />
                        <div className="w-100">
                          <div className="d-flex align-items-center justify-content-between">
                            <div>
                              <h5>{review.user_id}</h5>
                              <p>{new Date(review.review_date).toLocaleDateString("en-US", options)}</p>
                            </div>
                            <span className="d-flex align-items-center">
                              {review.rating} <i className="ri-star-fill"></i>
                            </span>
                          </div>
                        </div>
                      </div>
                    ))}
                  </ListGroup>
                </div>
              </div>
            </Col>
            <Col lg="4">
              <Booking tour={tour} avgRating={avgRating} />
            </Col>
          </Row>
        </Container>
      </section>
      <Newletters />
    </>
  );
};

export default TourDetails;
